INSERT INTO `newscenter` VALUES (1, '新聞', '2016-10-14', '苗栗最高齡(109歲)~就在苑裡鎮', '苑裡鎮長杜文卿今天上午帶領公所服務團隊，親赴本鎮也是全苗栗縣最高齡人瑞(109歲)黃郭蝦ㄚ嬤府上，並致贈慰問金6千元，祝福ㄚ嬤重陽佳節快樂！', '社會課', 'Yes', 2, '');
